import axios from 'axios';
import getConfig from 'next/config';

const { publicRuntimeConfig } = getConfig();
const baseURL: string = publicRuntimeConfig.BASE_URL || '';

const API = {
  get: async <Response>(path: string, params?: any, queries?: any, body?: any): Promise<Response | undefined> => {
    console.log(path);
    try {
      // const url = generateURL(path, params, queries);
      // console.log(url);
      // const response = await axios.get(url);
      const response = await axios.get('https://cors-anywhere.herokuapp.com/' + 'http://localhost:3000' + path);
      console.log(response);
      return response.data as Response;
    } catch (error) {
      if (error.response) {
        // The request was made and the server responded with a status code
        // that falls out of the range of 2xx
        console.error(error.response.data);
        console.error(error.response.status);
        console.error(error.response.headers);
      } else if (error.request) {
        // The request was made but no response was received
        // `error.request` is an instance of XMLHttpRequest in the browser and an instance of
        // http.ClientRequest in node.js
        console.error(error.request);
      } else {
        // Something happened in setting up the request that triggered an Error
        console.error('Error', error.message);
      }
      console.error(error.config);
    }
  }
};

const generateURL = (path: string, params?: any, queries?: any): string => {
  let finalURL = path.charAt(0) === '/' ? baseURL + path : baseURL + '/' + path;
  console.log('1', params);
  if (params) {
    console.log('2');
    const paramKeys = Object.getOwnPropertyNames(params);
    for (const p of paramKeys) {
      finalURL = finalURL.replace(`:${p}`, params[p]);
    }
  }
  console.log('2');
  if (queries) {
    const queryKeys = Object.getOwnPropertyNames(queries);
    const cleanedQueries = queryKeys.filter((k: string) => {
      return queries[k] !== undefined && queries[k] !== null;
    });

    if (0 === cleanedQueries.length) {
      return finalURL;
    }

    finalURL += '?';
    for (const q of cleanedQueries) {
      finalURL += `${q}=${queries[q]}&`;
    }
  }
  console.log('3');
  return finalURL.slice(0, -1);
};

export default API;
